import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { io } from 'socket.io-client';
import { AuthContext } from "../context/AuthContext";
import { authAPI, messgageAPI } from "../services/api";
import ChatBox from '../components/ChatBox';
import UserList from '../components/UserList';
import './Chat.css';

const Chat = () => {
    const { user, logout } = useContext(AuthContext);
    const [users, setUsers] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);
    const [messages, setMessages] = useState([]);
    const [socket, setSocket] = useState(null);
    const [onlineUsers, setOnlineUsers] = useState([]);
    const [isTyping, setIsTyping] = useState(false);
    const navigate = useNavigate();

    // Initialize Socket.io
    useEffect(() => {
        const newSocket = io(process.env.REACT_APP_API_URL);
        setSocket(newSocket);

        if (user) {
            newSocket.emit('user_online', user.id);
        }

        newSocket.on('users_online', (onlineUserIds) => {
            setOnlineUsers(onlineUserIds);
        });

        newSocket.on('receive_message', (data) => {
            setMessages(prev => [...prev, {
                sender: data.sender,
                text: data.text,
                timestamp: data.timestamp
            }]);
        });

        newSocket.on('user_typing', (data) => {
            setIsTyping(true);
        });

        newSocket.on('user_stopped_typing', () => {
            setIsTyping(false);
        });

        return () => newSocket.disconnect();
    }, [user]);

    // Fetch all users
    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const { data } = await authAPI.getUsers();
                setUsers(data);
            } catch (error) {
                console.error('Failed to fetch ussers');
            }
        };

        fetchUsers();
    }, []);

    // Fetch messags when user selected
    useEffect(() => {
        if (selectedUser) {
            const fetchMessges = async () => {
                try {
                    const { data } = await messgageAPI.getMessage(selectedUser._id);
                    setMessages(data);
                } catch (error) {
                    console.error('Failed to fetch messages');
                }
            };
            fetchMessges();
        }
    }, [selectedUser]);

    // Send message
    const handleSendMessage = (text) => {
        if (socket && selectedUser) {
            socket.emit('send_message', {
                sender: user.id,
                receiver: selectedUser._id,
                text
            });

            // Add to local messages immediately
            setMessages(prev => [...prev, {
                sender: user.id,
                text,
                timestamp: new Date()
            }]);
        }
    };

    // Typing indicator
    const handlyTyping = () => {
        if (socket && selectedUser) {
            socket.emit('typing', {
                receiver: selectedUser._id,
                senderName: user.username
            });
        }
    };

    const handleStopIyping = () => {
        if (socket && selectedUser) {
            socket.emit('stop_typing', {
                receiver: selectedUser._id
            });
        }
    };

    return (
        <div className="chat-container">
            <div className="header">
                <h1>Chat App</h1>
                <div className="user-info">
                    <span>{user?.username}</span>
                    <button
                        onClick={() => {
                            logout();
                            navigate('/login');
                        }}>Logout</button>
                </div>
            </div>

            <div className="chat-wrapper">
                <UserList
                    users={users}
                    selectedUser={selectedUser}
                    setOnlineUsers={onlineUsers}
                    onSelectUser={setSelectedUser}
                />

                <ChatBox
                    selectedUser={selectedUser}
                    messages={messages}
                    currentUser={user}
                    onSendMessage={handleSendMessage}
                    onTyping={handlyTyping}
                    onStopTyping={handleStopIyping}
                    isTyping={isTyping}
                />
            </div>
        </div>
    );
};

export default Chat;
