import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { io } from 'socket.io-client';
import { AuthContext } from "../context/AuthContext";
import { authAPI, messgageAPI } from "../services/api";
import ChatBox from '../components/ChatBox';
import UserList from '../components/UserList';
import './Chat.css';

const Chat = () => {
    const { user, logout } = useContext(AuthContext);
    const [users, setUsers] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);
    const [messages, setMessages] = useState([]);
    const [socket, setSocket] = useState(null);
    const [onlineUsers, setOnlineUsers] = useState([]);
    const [isTyping, setIsTyping] = useState(false);
    const navigate = useNavigate();

    // Initialize Socket.io
    useEffect(() => {
        const newSocket = io(process.env.REACT_APP_API_URL);
        setSocket(newSocket);

        if (user) {
            newSocket.emit('user_online', user.id);
        }

        newSocket.on('users_online', (onlineUserIds) => setOnlineUsers(onlineUserIds));

        newSocket.on('receive_message', (data) => {
            setMessages(prev => [...prev, {
                sender: data.sender,
                text: data.text,
                timestamp: data.timestamp,
                seen: data.seen || false,
                _id: data._id
            }]);
        });

        newSocket.on('user_typing', () => setIsTyping(true));
        newSocket.on('user_stopped_typing', () => setIsTyping(false));

        newSocket.on('messages_seen', ({ by }) => {
            setMessages(prev =>
                prev.map(msg =>
                    msg.sender === user.id && msg.receiver === by
                        ? { ...msg, seen: true }
                        : msg
                )
            );
        });

        return () => newSocket.disconnect();
    }, [user]);

    // Fetch all users
    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const { data } = await authAPI.getUsers();
                setUsers(data);
            } catch (error) {
                console.error('Failed to fetch users');
            }
        };
        fetchUsers();
    }, []);

    // Fetch messages when user selected
    useEffect(() => {
        if (selectedUser) {
            const fetchMessages = async () => {
                try {
                    const { data } = await messgageAPI.getMessage(selectedUser._id);
                    setMessages(data);
                } catch (error) {
                    console.error('Failed to fetch messages');
                }
            };
            fetchMessages();

            // Mark all messages from this user as seen
            if (socket) {
                socket.emit('mark_seen', { senderId: selectedUser._id, receiverId: user.id });
            }

            // Update local messages immediately
            setMessages(prev =>
                prev.map(msg =>
                    msg.sender === selectedUser._id && msg.receiver === user.id
                        ? { ...msg, seen: true }
                        : msg
                )
            );
        }
    }, [selectedUser, socket, user.id]);

    // Send message
    const handleSendMessage = (text) => {
        if (socket && selectedUser) {
            socket.emit('send_message', {
                sender: user.id,
                receiver: selectedUser._id,
                text
            });

            setMessages(prev => [...prev, {
                sender: user.id,
                receiver: selectedUser._id,
                text,
                timestamp: new Date(),
                seen: false
            }]);
        }
    };

    // Typing indicator
    const handleTyping = () => {
        if (socket && selectedUser) {
            socket.emit('typing', { receiver: selectedUser._id, senderName: user.username });
        }
    };

    const handleStopTyping = () => {
        if (socket && selectedUser) {
            socket.emit('stop_typing', { receiver: selectedUser._id });
        }
    };

    return (
        <div className="chat-container">
            <div className="header">
                <h1>Chat App</h1>
                <div className="user-info">
                    <span>{user?.username}</span>
                    <button onClick={() => { logout(); navigate('/login'); }}>Logout</button>
                </div>
            </div>

            <div className="chat-wrapper">
                <UserList
                    users={users}
                    selectedUser={selectedUser}
                    onlineUsers={onlineUsers}
                    onSelectUser={setSelectedUser}
                />

                <ChatBox
                    selectedUser={selectedUser}
                    messages={messages}
                    currentUser={user}
                    onSendMessage={handleSendMessage}
                    onTyping={handleTyping}
                    onStopTyping={handleStopTyping}
                    isTyping={isTyping}
                />
            </div>
        </div>
    );
};

export default Chat;
