import React, { useState, useEffect, useContext, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { io } from 'socket.io-client';
import { AuthContext } from '../context/AuthContext';
import { authAPI, messageAPI } from '../services/api';
import ChatBox from '../components/ChatBox';
import UserList from '../components/UserList';
import './Chat.css';
// Global listener for debugging
window.socketDebug = {
    eventsReceived: [],
    messagesSeen: []
};

const Chat = () => {
    const { user, logout } = useContext(AuthContext);
    const [users, setUsers] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);
    const [messages, setMessages] = useState([]);
    const [socket, setSocket] = useState(null);
    const [onlineUsers, setOnlineUsers] = useState([]);
    const [isTyping, setIsTyping] = useState(false);
    const navigate = useNavigate();

    // âœ… Initialize Socket Once
    useEffect(() => {
        if (!user?.id) {
            console.log('âŒ No user, skipping socket');
            return;
        }

        console.log('ðŸ”Œ Setting up Socket.io connection...');

        const newSocket = io(process.env.REACT_APP_API_URL, {
            reconnection: true,
            reconnectionDelay: 1000,
            reconnectionDelayMax: 5000,
            reconnectionAttempts: 5,
            transports: ['websocket', 'polling']
        });

        // Connection event
        newSocket.on('connect', () => {
            console.log('âœ… Socket connected:', newSocket.id);
            newSocket.emit('user_online', user.id);
        });

        // Online users list
        newSocket.on('users_online', (userIds) => {
            console.log('ðŸ“¡ Users online:', userIds);
            setOnlineUsers(Array.isArray(userIds) ? userIds : []);
        });

        // Receive messages from others
        newSocket.on('receive_message', (data) => {
            console.log('ðŸ’¬ Message received:', data);
            setMessages(prev => [...prev, {
                _id: data._id,
                sender: data.sender,
                text: data.text,
                seen: data.seen || false,
                timestamp: data.timestamp
            }]);
        });

        // Typing indicators
        newSocket.on('user_typing', () => {
            setIsTyping(true);
        });

        newSocket.on('user_stopped_typing', () => {
            setIsTyping(false);
        });

        // âœ… CRITICAL: Messages marked as seen
        newSocket.on('messages_seen', (data) => {
            console.log('ðŸ‘€ MESSAGES SEEN EVENT RECEIVED:', data);
            setMessages(prev => {
                const updated = prev.map(msg => {
                    // Mark all messages from current user as seen
                    if (msg.sender === user.id) {
                        console.log('  âœ… Marking message as seen:', msg._id);
                        return { ...msg, seen: true };
                    }
                    return msg;
                });
                console.log('  ðŸ“Š Updated messages count:', updated.length);
                return updated;
            });
        });

        newSocket.on('disconnect', () => {
            console.log('âŒ Socket disconnected');
        });

        setSocket(newSocket);

        return () => {
            console.log('ðŸ§¹ Cleaning up socket');
            newSocket.disconnect();
        };
    }, [user?.id]);

    // âœ… Fetch all users
    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const { data } = await authAPI.getUsers();
                setUsers(data || []);
            } catch (error) {
                console.error('âŒ Failed to fetch users:', error);
            }
        };

        fetchUsers();
    }, []);

    // âœ… Fetch messages and mark as seen
    useEffect(() => {
        if (!selectedUser || !user?.id || !socket) {
            return;
        }

        const loadMessages = async () => {
            try {
                console.log('ðŸ“¨ Fetching messages from:', selectedUser.username);
                const { data } = await messageAPI.getMessages(selectedUser._id);
                setMessages(data || []);

                // âœ… Mark messages as seen
                console.log('ðŸ‘€ Emitting mark_seen event');
                console.log('   From (sender):', selectedUser._id);
                console.log('   To (receiver):', user.id);

                socket.emit('mark_seen', {
                    senderId: selectedUser._id,
                    receiverId: user.id
                });
            } catch (error) {
                console.error('âŒ Failed to load messages:', error);
            }
        };

        loadMessages();
    }, [selectedUser, user?.id, socket]);

    // âœ… Send message
    const handleSendMessage = useCallback((text) => {
        if (!socket || !selectedUser || !user?.id) return;

        socket.emit('send_message', {
            sender: user.id,
            receiver: selectedUser._id,
            text
        });

        // Add to local state
        setMessages(prev => [...prev, {
            _id: Date.now(),
            sender: user.id,
            text,
            seen: false,
            timestamp: new Date()
        }]);
    }, [socket, selectedUser, user?.id]);

    // âœ… Handle typing
    const handleTyping = useCallback(() => {
        if (socket && selectedUser) {
            socket.emit('typing', {
                receiver: selectedUser._id,
                senderName: user.username
            });
        }
    }, [socket, selectedUser, user?.username]);

    // âœ… Stop typing
    const handleStopTyping = useCallback(() => {
        if (socket && selectedUser) {
            socket.emit('stop_typing', {
                receiver: selectedUser._id
            });
        }
    }, [socket, selectedUser]);

    // âœ… Logout
    const handleLogout = () => {
        if (socket) socket.disconnect();
        logout();
        navigate('/login');
    };

    return (
        <div className="chat-container">
            <div className="header">
                <h1>ðŸ’¬ Chat App</h1>
                <div className="user-info">
                    <span>ðŸ‘¤ {user?.username}</span>
                    <button onClick={handleLogout}>Logout</button>
                </div>
            </div>

            <div className="chat-wrapper">
                <UserList
                    users={users}
                    selectedUser={selectedUser}
                    onlineUsers={onlineUsers}
                    onSelectUser={setSelectedUser}
                />

                <ChatBox
                    selectedUser={selectedUser}
                    messages={messages}
                    currentUser={user}
                    onSendMessage={handleSendMessage}
                    onTyping={handleTyping}
                    onStopTyping={handleStopTyping}
                    isTyping={isTyping}
                />
            </div>
        </div>
    );
};

export default Chat;
