import React, { useState, useEffect, useContext, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { io } from "socket.io-client";
import { AuthContext } from "../context/AuthContext";
import { authAPI, messgageAPI } from "../services/api";
import ChatBox from "../components/ChatBox";
import UserList from "../components/UserList";
import "./Chat.css";

const Chat = () => {
    const { user, logout } = useContext(AuthContext);
    const [users, setUsers] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);
    const [messages, setMessages] = useState([]);
    const [socket, setSocket] = useState(null);
    const [onlineUsers, setOnlineUsers] = useState([]);
    const [isTyping, setIsTyping] = useState(false);
    const navigate = useNavigate();

    // ----------------- SOCKET.IO -----------------
    useEffect(() => {
        const newSocket = io(process.env.REACT_APP_API_URL);
        setSocket(newSocket);

        if (user) {
            newSocket.emit("user_online", user.id);
        }

        newSocket.on("users_online", (onlineUserIds) => setOnlineUsers(onlineUserIds));

        newSocket.on("receive_message", (data) => {
            setMessages((prev) => [...prev, data]);
        });

        newSocket.on("user_typing", () => setIsTyping(true));
        newSocket.on("user_stopped_typing", () => setIsTyping(false));

        newSocket.on("messages_seen", ({ by }) => {
            setMessages((prev) =>
                prev.map((msg) =>
                    msg.sender === user.id && msg.receiver === by ? { ...msg, seen: true } : msg
                )
            );
        });

        return () => newSocket.disconnect();
    }, [user]);

    // ----------------- FETCH USERS -----------------
    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const { data } = await authAPI.getUsers();
                setUsers(data);
            } catch (err) {
                console.error("Failed to fetch users:", err);
            }
        };
        fetchUsers();
    }, []);

    // ----------------- FETCH MESSAGES -----------------
    useEffect(() => {
        if (selectedUser) {
            const fetchMessages = async () => {
                try {
                    const { data } = await messgageAPI.getMessage(selectedUser._id);
                    setMessages(data);
                } catch (err) {
                    console.error("Failed to fetch messages:", err);
                }
            };
            fetchMessages();
        }
    }, [selectedUser]);

    // ----------------- MARK AS SEEN -----------------
    useEffect(() => {
        if (selectedUser && socket) {
            socket.emit("mark_seen", { senderId: selectedUser._id, receiverId: user.id });

            setMessages((prev) =>
                prev.map((msg) =>
                    msg.sender === selectedUser._id && msg.receiver === user.id
                        ? { ...msg, seen: true }
                        : msg
                )
            );
        }
    }, [selectedUser, socket]);

    // ----------------- SEND MESSAGE -----------------
    const handleSendMessage = (text) => {
        if (!socket || !selectedUser) return;

        const messageData = {
            sender: user.id,
            receiver: selectedUser._id,
            text,
            timestamp: new Date(),
            seen: false,
        };

        socket.emit("send_message", messageData);
        setMessages((prev) => [...prev, messageData]);
    };

    // ----------------- TYPING INDICATOR -----------------
    const handleTyping = () => {
        if (socket && selectedUser) {
            socket.emit("typing", { receiver: selectedUser._id });
        }
    };

    const handleStopTyping = () => {
        if (socket && selectedUser) {
            socket.emit("stop_typing", { receiver: selectedUser._id });
        }
    };

    return (
        <div className="chat-container">
            <div className="header">
                <h1>Chat App</h1>
                <div className="user-info">
                    <span>{user?.username}</span>
                    <button
                        onClick={() => {
                            logout();
                            navigate("/login");
                        }}
                    >
                        Logout
                    </button>
                </div>
            </div>

            <div className="chat-wrapper">
                <UserList
                    users={users}
                    selectedUser={selectedUser}
                    onlineUsers={onlineUsers}
                    onSelectUser={setSelectedUser}
                />

                <ChatBox
                    selectedUser={selectedUser}
                    messages={messages}
                    currentUser={user}
                    onSendMessage={handleSendMessage}
                    onTyping={handleTyping}
                    onStopTyping={handleStopTyping}
                    isTyping={isTyping}
                />
            </div>
        </div>
    );
};

export default Chat;
