import React, { useEffect, useRef } from "react";
import MessageInput from './MessageInput';
import './ChatBox.css';

const ChatBox = ({ selectedUser, messages, currentUser, onSendMessage, onTyping, onStopTyping, isTyping }) => {
    const messagesEndRef = useRef(null);

    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    }, [messages]);

    if (!selectedUser) return <div className="chat-box empty">Select a user to start chatting</div>;

    return (
        <div className="chat-box">
            <div className="chat-header"><h3>{selectedUser.username}</h3></div>

            <div className="messages">
                {messages.map((msg) => (
                    <div key={msg._id || Math.random()} className={`message ${msg.sender === currentUser.id ? 'sent' : 'received'}`}>
                        <p>{msg.text}</p>
                        <span className="timestamp">{new Date(msg.timestamp).toLocaleTimeString()}</span>
                        {msg.sender === currentUser.id && (
                            <span className="message-status">{msg.seen ? '✔✔' : '✔'}</span>
                        )}
                    </div>
                ))}
                {isTyping && <div className="typing">{selectedUser.username} is typing...</div>}
                <div ref={messagesEndRef} />
            </div>

            <MessageInput onSendMessage={onSendMessage} onTyping={onTyping} onStopTyping={onStopTyping} />
        </div>
    );
};

export default ChatBox;
