import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { io } from 'socket.io-client';
import { AuthContext } from '../context/AuthContext';
import { authAPI, messageAPI } from '../services/api';
import ChatBox from '../components/ChatBox';
import UserList from '../components/UserList';
import './Chat.css';

const Chat = () => {
    const { user, logout } = useContext(AuthContext);
    const [users, setUsers] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);
    const [messages, setMessages] = useState([]);
    const [socket, setSocket] = useState(null);
    const [onlineUsers, setOnlineUsers] = useState([]);
    const [isTyping, setIsTyping] = useState(false);
    const navigate = useNavigate();

    // Initialize Socket
    useEffect(() => {
        if (!user) {
            console.log('âŒ No user, skipping socket connection');
            return;
        }

        console.log('ðŸ”Œ Setting up socket connection...');

        const newSocket = io(process.env.REACT_APP_API_URL, {
            reconnection: true,
            reconnectionDelay: 1000,
            reconnectionDelayMax: 5000,
            reconnectionAttempts: 5,
            transports: ['websocket', 'polling']
        });

        // Connection events
        newSocket.on('connect', () => {
            console.log('âœ… Socket connected:', newSocket.id);
            newSocket.emit('user_online', user.id);
        });

        newSocket.on('users_online', (userIds) => {
            console.log('ðŸ“¡ Received users_online:', userIds);
            setOnlineUsers(Array.isArray(userIds) ? userIds : []);
        });

        // Receive message from someone else
        newSocket.on('receive_message', (data) => {
            console.log('ðŸ’¬ Message received:', data);
            setMessages(prev => [...prev, {
                _id: data._id,
                sender: data.sender,
                text: data.text,
                seen: data.seen || false,
                timestamp: data.timestamp
            }]);
        });

        // Your messages were marked as seen by someone
        newSocket.on('messages_seen', (data) => {
            console.log('ðŸ‘€ Messages marked as seen by:', data.by);
            console.log('ðŸ“¤ Updating messages state...');
            console.log('ðŸŽ‰ MESSAGES_SEEN EVENT RECEIVED!');
            console.log('   Event data:', data);
            console.log('   Current user ID:', user.id);
            console.log('   Received from:', data.by);

            // Update all messages from current user to show seen status
            setMessages(prev => {
                const updated = prev.map(msg => {
                    if (msg.sender === user.id && msg.receiver === data.by) {
                        console.log('âœ… Marking message as seen:', msg._id);
                        return { ...msg, seen: true };
                    }
                    return msg;
                });
                console.log('ðŸ“Š Updated messages:', updated);
                return updated;
            });
        });

        newSocket.on('user_typing', (data) => {
            console.log('âœï¸ User typing');
            setIsTyping(true);
        });

        newSocket.on('user_stopped_typing', () => {
            console.log('âœï¸ User stopped typing');
            setIsTyping(false);
        });

        newSocket.on('disconnect', () => {
            console.log('âŒ Socket disconnected');
            setOnlineUsers([]);
        });

        newSocket.on('error', (error) => {
            console.error('âš ï¸  Socket error:', error);
        });

        setSocket(newSocket);

        return () => {
            console.log('ðŸ§¹ Cleaning up socket...');
            newSocket.disconnect();
        };
    }, [user]);

    // Fetch all users
    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const { data } = await authAPI.getUsers();
                console.log('ðŸ‘¥ Users fetched:', data);
                setUsers(data || []);
            } catch (error) {
                console.error('âŒ Failed to fetch users:', error);
                setUsers([]);
            }
        };

        fetchUsers();
    }, []);

    // Fetch messages when user selected + Mark as seen
    useEffect(() => {
        if (!selectedUser || !user || !socket) {
            console.log('âš ï¸  Cannot fetch messages - missing:', {
                selectedUser: !!selectedUser,
                user: !!user,
                socket: !!socket
            });
            return;
        }

        const fetchMessages = async () => {
            try {
                console.log('ðŸ“¨ Fetching messages for:', selectedUser._id);
                const { data } = await messageAPI.getMessages(selectedUser._id);
                console.log('ðŸ“¥ Messages fetched:', data);
                setMessages(data || []);

                // Mark messages as seen immediately
                console.log('ðŸ‘€ About to emit mark_seen event');
                console.log('   Sender (who sent messages):', selectedUser._id);
                console.log('   Receiver (you):', user.id);

                socket.emit('mark_seen', {
                    senderId: selectedUser._id,  // The person who sent the messages
                    receiverId: user.id          // You (current user)
                });

                console.log('âœ… mark_seen event emitted');
            } catch (error) {
                console.error('âŒ Failed to fetch messages:', error);
                setMessages([]);
            }
        };

        fetchMessages();
    }, [selectedUser, user, socket]);

    // Send message
    const handleSendMessage = (text) => {
        if (!socket || !selectedUser || !user) {
            console.error('âŒ Cannot send - missing socket, selectedUser, or user');
            return;
        }

        const messageData = {
            sender: user.id,
            receiver: selectedUser._id,
            text
        };

        console.log('ðŸ“¤ Sending message:', messageData);
        socket.emit('send_message', messageData);

        // Add to local messages immediately
        setMessages(prev => [...prev, {
            _id: new Date().getTime(),
            sender: user.id,
            text,
            timestamp: new Date(),
            seen: false
        }]);
    };

    const handleTyping = () => {
        if (socket && selectedUser) {
            socket.emit('typing', {
                receiver: selectedUser._id,
                senderName: user.username
            });
        }
    };

    const handleStopTyping = () => {
        if (socket && selectedUser) {
            socket.emit('stop_typing', {
                receiver: selectedUser._id
            });
        }
    };

    const handleLogout = () => {
        if (socket) socket.disconnect();
        logout();
        navigate('/login');
    };

    return (
        <div className="chat-container">
            <div className="header">
                <h1>ðŸ’¬ Chat App</h1>
                <div className="user-info">
                    <span>{user?.username || 'User'}</span>
                    <button onClick={handleLogout}>Logout</button>
                </div>
            </div>

            <div className="chat-wrapper">
                <UserList
                    users={users}
                    selectedUser={selectedUser}
                    onlineUsers={onlineUsers}
                    onSelectUser={setSelectedUser}
                />

                <ChatBox
                    selectedUser={selectedUser}
                    messages={messages}
                    currentUser={user}
                    onSendMessage={handleSendMessage}
                    onTyping={handleTyping}
                    onStopTyping={handleStopTyping}
                    isTyping={isTyping}
                />
            </div>
        </div>
    );
};

export default Chat;
