import React, { useState, useRef, useEffect } from 'react';
import MessageInput from './MessageInput';
import './ChatBox.css';

const ChatBox = ({
    selectedUser,
    messages = [],
    currentUser,
    onSendMessage,
    onTyping,
    onStopTyping,
    isTyping
}) => {
    const messagesEndRef = useRef(null);

    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    }, [messages]);

    if (!selectedUser) {
        return (
            <div className="chat-box empty">
                <p>üëà Select a contact to start chatting</p>
            </div>
        );
    }

    return (
        <div className="chat-box">
            <div className="chat-header">
                <h3>{selectedUser.username}</h3>
            </div>

            <div className="messages">
                {Array.isArray(messages) && messages.length > 0 ? (
                    messages.map((msg, index) => {
                        const isSent = msg.sender === currentUser?.id;

                        return (
                            <div
                                key={msg._id || index}
                                className={`message ${isSent ? 'sent' : 'received'}`}
                            >
                                <p>{msg.text}</p>
                                <span className="timestamp">
                                    {new Date(msg.timestamp).toLocaleTimeString([], {
                                        hour: '2-digit',
                                        minute: '2-digit'
                                    })}

                                    {/* Show check marks only for sent messages */}
                                    {isSent && (
                                        <span className={`seen-status ${msg.seen ? 'seen' : 'unseen'}`}>
                                            {msg.seen ? ' ‚úì‚úì' : ' ‚úì'}
                                        </span>
                                    )}
                                </span>
                            </div>
                        );
                    })
                ) : (
                    <p style={{ textAlign: 'center', color: '#999', marginTop: '20px' }}>
                        No messages yet. Start the conversation! üëã
                    </p>
                )}
                {isTyping && <div className="typing">‚úèÔ∏è typing...</div>}
                <div ref={messagesEndRef} />
            </div>

            <MessageInput
                onSendMessage={onSendMessage}
                onTyping={onTyping}
                onStopTyping={onStopTyping}
            />
        </div>
    );
};

export default ChatBox;
